    if isinstance(data, dict):
        if depth &gt; max_depth:
            return '(...)'
        return {key: describe_data(value, depth + 1) for key, value in data.items()}
    elif isinstance(data, list) and data:
        if depth &gt; max_depth:
            return '(...)'
        return [describe_data(data[0], depth + 1)]
    else:
        return type(data)

